// Find the @media section and fix it
// Lines 417-440 have the corrupted mobile CSS
// Replace with clean media query

const correctMobileCSS = `    @media (max-width: 600px) {
      .page { padding: 0 16px; }
      .hero { padding: 32px 0 24px; }
      .hero h1 { font-size: 22px; }
      .hero-links { gap: 10px; }
      .section { padding: 24px 0; }
    }`;

// Find the line numbers
let startLine = -1;
let endLine = -1;
for (let i = 0; i < lines.length; i++) {
  if (lines[i].includes('@media (max-width: 600px)')) {
    startLine = i;
  }
  if (startLine !== -1 && lines[i].trim() === '}') {
    // Check if this is the closing brace of the media query
    // Look for the next line being </style>
    if (i + 1 < lines.length && lines[i + 1].includes('</style>')) {
      endLine = i;
      break;
    }
  }
}

console.log('Found @media at line', startLine + 1, 'to', endLine + 1);

// Keep lines before startLine, replace startLine to endLine, keep rest after endLine
const before = lines.slice(0, startLine);
const after = lines.slice(endLine + 1);

const result = [...before, ...correctMobileCSS.split('\n'), ...after].join('\n');
fs.writeFileSync('docs/index.html', result);
console.log('Fixed! Lines before:', lines.length, 'Lines after:', result.split('\n').length);
EOF
