const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const wallet = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';

async function check() {
  const eth = await provider.getBalance(wallet);
  const usdc = await provider.call({
    to: '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913',
    data: '0x70a08231' + wallet.slice(2).padStart(64, '0')
  });
  const usdcBalance = ethers.getBigInt(usdc);
  console.log('ETH:', ethers.formatEther(eth));
  console.log('USDC:', Number(usdcBalance) / 1e6);
}
check();
