const { ethers } = require('ethers');
const p = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const w = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, p);

async function main() {
  const ethBal = await p.getBalance(w.address);
  console.log('ETH:', ethers.formatEther(ethBal));

  // USDC on Base
  const usdc = new ethers.Contract('0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913', ['function balanceOf(address) view returns (uint256)'], p);
  const usdcBal = await usdc.balanceOf(w.address);
  console.log('USDC:', ethers.formatUnits(usdcBal, 6));

  // DAIMON
  const daimon = new ethers.Contract('0x98c51C8E958ccCD37F798b2B9332d148E2c05D57', ['function balanceOf(address) view returns (uint256)'], p);
  const daimonBal = await daimon.balanceOf(w.address);
  console.log('DAIMON:', ethers.formatUnits(daimonBal, 18));
}
main();
""}
