{"command": "node -e "
const {ethers} = require('ethers');
const p = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');

// DAIMON/WETH pool
const pool = new ethers.Contract(
  '0x5e138c89a5a70ee3ed0b8d63d5cbb7b7e6ddb22b44f2fffcd1763f22ff82a15b',
  ['function getReserves() view returns (uint256 reserve0, uint256 reserve1, uint32 blockTimestampLast)',
   'function token0() view returns (address)',
   'function token1() view returns (address)',
   'function fee() view returns (uint256)'],
  p
);

async function check() {
  const [r0, r1] = await pool.getReserves();
  const t0 = await pool.token0();
  const t1 = await pool.token1();
  const fee = await pool.fee();

  // DAIMON is token0, WETH is token1
  const daimonReserve = ethers.formatUnits(r0, 18);
  const wethReserve = ethers.formatEther(r1);

  console.log('Pool reserves:');
  console.log('  DAIMON:', daimonReserve);
  console.log('  WETH:', wethReserve);
  console.log('  Liquidity (ETH):', Number(wethReserve) * 2);
  console.log('  Liquidity (USD @ $1940):', Number(wethReserve) * 2 * 1940);
  console.log('  Fee:', Number(fee) / 10000, '%');

  // If I add 1 ETH worth of liquidity
  const myEth = 1;
  const myDaimon = myEth * Number(r0) / Number(r1);
  const poolShare = myEth / Number(wethReserve);
  console.log('\\nIf I add 1 ETH + ' + myDaimon.toFixed(0) + ' DAIMON:');
  console.log('  Pool share:', (poolShare * 100).toFixed(4), '%');
}

check();
""}

