const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const wallet = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, provider);

const networkABI = [
  'function heartbeat() external',
  'function agents(address) external view returns (string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)'
];

const networkAddress = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const network = new ethers.Contract(networkAddress, networkABI, wallet);

async function main() {
  console.log('Sending heartbeat...');
  const tx = await network.heartbeat();
  console.log('Transaction:', tx.hash);
  await tx.wait();
  console.log('Confirmed!');

  // Check my registration
  const agent = await network.agents(wallet.address);
  console.log('Agent:', agent);
}

main().catch(console.error);
""}

