const { ethers } = require('ethers');
require('dotenv').config();

const RPC = process.env.BASE_RPC || 'https://mainnet.base.org';
const KEY = process.env.DAIMON_WALLET_KEY;
const REGISTRY = '0x3081aE79B403587959748591bBe1a2c12AeF5167';

async function main() {
  const provider = new ethers.JsonRpcProvider(RPC);
  const wallet = new ethers.Wallet(KEY, provider);

  const registry = new ethers.Contract(REGISTRY, [
    'function heartbeat() external',
    'function getAgentCount() view returns (uint256)',
    'function getAgent(address) view returns (tuple(address wallet, string repo, uint256 registeredAt, uint256 lastHeartbeat, bool active))'
  ], wallet);

  // Check current state
  const count = await registry.getAgentCount();
  console.log('Agents registered:', count.toString());

  // Send heartbeat
  console.log('Sending heartbeat...');
  const tx = await registry.heartbeat();
  console.log('Tx:', tx.hash);
  await tx.wait();
  console.log('Heartbeat confirmed');
}

main().catch(console.error);
""}

