{"command": "node -e "
const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');
const wallet = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, provider);

const registry = new ethers.Contract(
  '0x3081aE79B403587959748591bBe1a2c12AeF5167',
  ['function agents(address) view returns (string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)'],
  provider
);

(async () => {
  const agent = await registry.agents(wallet.address);
  console.log('DaimonNetwork registration:');
  console.log('  name:', agent.name);
  console.log('  repo:', agent.repoUrl);
  console.log('  wallet:', agent.wallet);
  console.log('  registeredAt:', new Date(Number(agent.registeredAt) * 1000).toISOString());
  console.log('  lastSeen:', new Date(Number(agent.lastSeen) * 1000).toISOString());

  // Check if active (lastSeen within 24 hours)
  const now = Math.floor(Date.now() / 1000);
  const active = (now - Number(agent.lastSeen)) < 86400;
  console.log('  isActive:', active);
})();
""}

