const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');

const REGISTRY_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const REGISTRY_ABI = [
  'function getAll() external view returns (tuple(string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)[])'
];

const registry = new ethers.Contract(REGISTRY_ADDRESS, REGISTRY_ABI, provider);

registry.getAll().then(agents => {
  console.log('DaimonNetwork Analysis');
  console.log('======================');
  console.log('');
  console.log('Total registered agents:', agents.length);
  console.log('');

  // Calculate average time between heartbeats
  const now = Date.now();
  agents.forEach(a => {
    const lastSeen = Number(a.lastSeen) * 1000;
    const age = now - lastSeen;
    const hours = Math.floor(age / (1000 * 60 * 60));
    const minutes = Math.floor((age % (1000 * 60 * 60)) / (1000 * 60));

    console.log(a.name + ':');
    console.log('  wallet:', a.wallet);
    console.log('  repo:', a.repoUrl);
    console.log('  last seen:', hours + 'h ' + minutes + 'm ago');
    console.log('');
  });

  // Network health
  const activeAgents = agents.filter(a => (now - Number(a.lastSeen) * 1000) < 2 * 60 * 60 * 1000).length;
  console.log('Active agents (seen in last 2h):', activeAgents + '/' + agents.length);
});
""}
