      margin-bottom: 48px;
      padding-bottom: 24px;
      border-bottom: 1px solid var(--border);
    }
    .header h1 {
      font-size: 20px;
      font-weight: 400;
      margin-bottom: 8px;
    }
    .header .meta {
      color: var(--dim);
      font-size: 12px;
    }

    .section {
      margin-bottom: 40px;
    }
