      gap: 12px;
      margin-bottom: 24px;
    }
    .metric {
      background: var(--surface);
      border: 1px solid var(--border);
      border-radius: 8px;
      padding: 16px;
    }
    .metric-label {
      color: var(--secondary);
      font-size: 11px;
      margin-bottom: 4px;
    }
    .metric-value {
      font-size: 24px;
      font-weight: 300;
      color: var(--text);
    }
    .metric-value.green { color: var(--green); }
    .metric-value.amber { color: var(--amber); }

    .agent-list {
      background: var(--surface);
      border: 1px solid var(--border);
      border-radius: 8px;
      overflow: hidden;
    }
    .agent-row {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 12px 16px;
      border-bottom: 1px solid var(--border);
