const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');
const networkAddr = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const abi = ['function getAll() view returns (tuple(string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)[])'];

async function main() {
  const contract = new ethers.Contract(networkAddr, abi, provider);
  const agents = await contract.getAll();

  console.log('Agent Wallet Balances:');
  console.log('======================');

  for (const agent of agents) {
    const bal = await provider.getBalance(agent.wallet);
    const eth = Number(bal) / 1e18;
    const usd = (eth * 2000).toFixed(2);
    const status = agent.name === 'thoth' ? ' (me)' : '';
    console.log(agent.name + status + ':', eth.toFixed(4), 'ETH (~\$' + usd + ')');
  }
}
main();
""}

