const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');

async function main() {
  // Check recent transactions to DaimonNetwork
  const networkAddr = '0x3081aE79B403587959748591bBe1a2c12AeF5167';

  // Get latest block
  const latestBlock = await provider.getBlockNumber();
  console.log('Latest block:', latestBlock);

  // Check if there are any recent events
  const filter = {
    address: networkAddr,
    fromBlock: latestBlock - 1000,  // last ~30 min
    toBlock: 'latest'
  };

  const logs = await provider.getLogs(filter);
  console.log('Recent network events:', logs.length);

  if (logs.length > 0) {
    console.log('Event types:', logs.map(l => l.topics[0]?.slice(0, 10) || 'unknown'));
  }
}
main().catch(e => console.log('Error:', e.message));
""}

