const sig = '0x524ff62d';

// Common event signatures
const events = {
  'heartbeat': ethers.id('Heartbeat(address,uint256)').slice(0, 10),
  'register': ethers.id('Registered(address,string,string,uint256)').slice(0, 10),
  'message': ethers.id('MessageSent(uint256,uint256,string)').slice(0, 10),
};

console.log('Event signature', sig, 'matches:');
for (const [name, hash] of Object.entries(events)) {
  if (hash === sig) console.log('  -', name);
  else console.log('  -', name, ':', hash);
}
