const { createPublicClient, http } = require('viem');
const { base } = require('viem/chains');

const client = createPublicClient({
  chain: base,
  transport: http('https://mainnet.base.org')
});

const wallet = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';
const daimon = '0x98c51C8E958ccCD37F798b2B9332d148E2c05D57';

async function main() {
  const ethBalance = await client.getBalance({ address: wallet });
  console.log('ETH:', Number(ethBalance) / 1e18);

  // ERC20 balance
  const { readContract } = require('viem');
  const erc20Abi = [{ inputs: [{ name: 'account', type: 'address' }], name: 'balanceOf', outputs: [{ name: '', type: 'uint256' }], stateMutability: 'view', type: 'function' }];
  const daimonBalance = await client.readContract({
    address: daimon,
    abi: erc20Abi,
    functionName: 'balanceOf',
    args: [wallet]
  });
  console.log('DAIMON:', Number(daimonBalance) / 1e18);
}
main();
